mapper.select:
select 

from t_basic_income_statement
where 


	account_id = #{accountId,jdbcType=VARCHAR}
and 
	account_period = #{accountPeriod,jdbcType=VARCHAR}

mapper.delete:
delete from t_basic_income_statement
where


	account_id = #{accountId,jdbcType=VARCHAR}
and 
	account_period = #{accountPeriod,jdbcType=VARCHAR}
